import React, {Component} from "react";
import City from "./city.js";
import "./map.scss";

var transports = [
    {
        name: "Warszawa",
        price: 0
    },
    {
        name: "Łódź",
        price: 0
    },
    {
        name: "Katowice",
        price: 0
    },
    {
        name: "Wrocław",
        price: 0
    },
    {
        name: "Szczecin",
        price: 50
    },

    {
        name: "Kraków",
        price: 100
    },
    {
        name: "Białystok",
        price: 50
    },
    {
        name: "Poznań",
        price: 50
    },
    {
        name: "Rzeszów",
        price: 50
    },
];

let giveNewProps = (arr) => {
    arr.map((el) => {
        if (el.name === "Warszawa") {
            el.top = "145px";
            el.left = "235px";
        }
        if (el.name === "Poznań") {
            el.top = "135px";
            el.left = "75px";
        }
        if (el.name === "Wrocław") {
            el.top = "220px";
            el.left = "80px";
        }
        if (el.name === "Łódź") {
            el.top = "175px";
            el.left = "175px";
        }
        if (el.name === "Katowice") {
            el.top = "270px";
            el.left = "155px";
        }
        if (el.name === "Kraków") {
            el.top = "290px";
            el.left = "210px";
        }
        if (el.name === "Trójmiasto") {
            el.top = "35px";
            el.left = "140px";
        }
        if (el.name === "Szczecin") {
            el.top = "65px";
            el.left = "-5px";
        }
        if (el.name === "Białystok") {
            el.top = "50px";
            el.left = "300px";
        }
        if (el.name === "Rzeszów") {
            el.top = "285px";
            el.left = "300px";
        }
        if (el.price === 0) {
            el.text = "Brak dopłat, wyjazd gwarantowany"
        }
        if (el.price > 0) {
            el.text = `Dopłata ${el.price} PLN. Wyjazd możliwy pod warunkiem zebrania się grupy min. 15 osób`
        }
    });


    return arr
};

giveNewProps(transports);
console.log(transports);


class Map extends Component {

    render() {
        return (
            <div className="map-wrapper">
                <div className="map">
                    {transports.map((el) =>
                        <City top={el.top} left={el.left} name={el.name} text={el.text}/>
                    )}
                </div>
            </div>
        )
    }
}

export default Map;
