import React, {Component} from "react";
import "./city.scss";


class City extends Component {

    state = {
        textWindowHover: false
    };



    textWindow = () => {
        return (
            <div className="cityWindow"> Dopłata z wyjazd z tego miast to: {this.props.addPayment}
                    Odjazd gwarantowany przy liczbie osób: 15
            </div>
        )
    };

    render() {
        let style = {
            position: "absolute",
            left: this.props.left,
            top: this.props.top
        };
        return (
                <div className="object-city" style={style}>{this.props.name}
                    onMouseEnter={() => this.setState({ textWindowHover: true })}
                    onMouseLeave={() => this.setState({ textWindowHover: false })}
                <div className="autokar-wrapper"></div>
                    { this.state.textWindowHover ? this.textWindow() : null }
                </div>
        )
    }
}

export default City;
